package Lec1;

public class Arithmetic_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 7;
		int b = 9;
		int c = a + b;
		System.out.println(c);
		 c = a-b;
		 System.out.println(c);
		 int d = a*b;
		 System.out.println(d);
		 d=a/b;
		 System.out.println(d);
		 d=a%b;
		 System.out.println(d);

	}

}
