package Lec1;

import java.util.Scanner;

public class Loop_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int i = 0;
		while (i < n) {
			System.out.println("Hello");

			i += 1;// i=i+1;

		}

	}

}
