package Lec1;

public class Arithmetic_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 9;
		int b = 11;
		int c = a + b;
		System.out.println(c);
		c = a - b;// -2
		System.out.println(c);
		int d = a * b;
		System.out.println(d);
		int e = a / b;
		System.out.println(e);
		e = c * b;
		System.out.println(e);
	}

}
